let links=document.querySelectorAll(".link"),section=document.querySelectorAll("section");function activeLink(){links.forEach(e=>e.classList.remove("active")),this.classList.add("active")}links.forEach(e=>e.addEventListener("click",activeLink)),window.onscroll=()=>{section.forEach(e=>{var t=window.scrollY,i=e.offsetTop,c=e.offsetHeight,s=e.getAttribute("id");i<=t&&t<i+c&&links.forEach(e=>{e.classList.remove("active"),e.getAttribute("href")=="#"+s&&e.classList.add("active")})})};